package com.kortisan.framework.redux.controllers.notificationsimport com.kortisan.framework.redux.controllers.tagging.decorators.SceneBaseDecoratorimport com.kortisan.framework.redux.controllers.tagging.decorators.SceneDecoratorInterfaceclass FirebaseMessagingDecorator(    override val sceneDecorator: SceneDecoratorInterface): SceneBaseDecorator() {    fun setPushId(pushId: String): FirebaseMessagingDecorator =        apply { decoratedValues["id_push"] = pushId }    fun setElement(element: String): FirebaseMessagingDecorator =        apply { decoratedValues["element"] = element }    fun setType(type: String): FirebaseMessagingDecorator =        apply { decoratedValues["type"] = type }    fun setFlowName(flowName: String): FirebaseMessagingDecorator =        apply { decoratedValues["flowName"] = flowName }}